mapper.select:
SELECT c.id, c.name, c.address, c.handler, c.mobile_telephone, c.fixed_telephone, c.id_card, c.tax_number, c.verification, c.application_coding, concat_ws('??', c.material_one, c.material_two, c.material_three) as material_one, c.status, c.create_time FROM my_customer c
AND c.status = #{status}
AND c.handler like CONCAT('%', #{handler}, '%') or c.name like CONCAT('%', #{handler}, '%')
AND c.mobile_telephone like CONCAT('%', #{mobileTelephone}, '%') or c.application_coding like CONCAT('%', #{mobileTelephone}, '%') or c.fixed_telephone like CONCAT('%', #{mobileTelephone}, '%')
AND c.create_time between #{startTime} and #{endTime}
order by c.create_time desc
